package org.aoc22.daySix

val codeSequence = """"""
val testInput = "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"

fun findMarkerIndex(dataStream: String, markerLength: Int): Int {
    val iterator = dataStream.iterator()
    var buffer = ""

    while (buffer.length < markerLength) {
        val currentChar = iterator.next()
        println("$buffer $currentChar")
        if (buffer.contains(currentChar)) {
            buffer = buffer.substringAfterLast(currentChar) + currentChar
        } else {
            buffer += currentChar
        }
    }
    return dataStream.indexOf(buffer) + markerLength
}

fun main() {
    val messageStream = codeSequence
    val startMarkerIndex = findMarkerIndex(messageStream, 4)

    println("Start marker complete after index $startMarkerIndex")

    val messageMarkerIndex = findMarkerIndex(messageStream, 14)

    println("Message marker complete after index $messageMarkerIndex")
}